import React, { useState } from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Items from "./components/Items";

export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [
              {
                "id": 1,
                "title": "Grey Backpack",
                "img": "https://unsplash.com/photos/gray-backpack-LqYrhUIJrbA",
                "desc": "A versatile grey backpack perfect for all your travel needs.",
                "category": "backpacks",
                "price": "49.99"
              },
              {
                "id": 2,
                "title": "Mountain Tent",
                "img": "https://unsplash.com/photos/mountain-tent",
                "desc": "A durable tent designed for mountain adventures.",
                "category": "tents",
                "price": "149.99"
              },
              {
                "id": 3,
                "title": "Travel Sleeping Bag",
                "img": "https://unsplash.com/photos/travel-sleeping-bag",
                "desc": "Comfortable sleeping bag for a good night's sleep outdoors.",
                "category": "sleeping bags",
                "price": "79.99"
              },
              {
                "id": 4,
                "title": "Camping Stove",
                "img": "https://unsplash.com/photos/camping-stove",
                "desc": "Portable stove for cooking meals during your camping trips.",
                "category": "camping gear",
                "price": "39.99"
              },
              {
                "id": 5,
                "title": "Hiking Boots",
                "img": "https://unsplash.com/photos/hiking-boots",
                "desc": "Durable hiking boots for all terrains.",
                "category": "footwear",
                "price": "89.99"
              },
              {
                "id": 6,
                "title": "Travel Backpack",
                "img": "https://unsplash.com/photos/travel-backpack",
                "desc": "Spacious backpack for all your travel essentials.",
                "category": "backpacks",
                "price": "59.99"
              },
              {
                "id": 7,
                "title": "Portable Water Filter",
                "img": "https://unsplash.com/photos/portable-water-filter",
                "desc": "Ensure clean drinking water with this portable filter.",
                "category": "camping gear",
                "price": "29.99"
              },
              {
                "id": 8,
                "title": "Outdoor Jacket",
                "img": "https://unsplash.com/photos/outdoor-jacket",
                "desc": "Weatherproof jacket for all outdoor activities.",
                "category": "clothing",
                "price": "99.99"
              }
            ]

        }
    }



    render() {
        return (
            <div className="wrapper">
                <Header />
                <Items items={this.state.items}/>
                <Footer />
            </div>
        );
    }
}
